mapper.select:
SELECT g.id id, g.code code, g.type_name typeName, g.name 'name', g.remarks remarks, g.op_user opUser, g.op_time opTime, date_format(g.op_time,'%Y/%m/%d') opTimeStr, CASE WHEN d.num IS NULL THEN 0 ELSE d.num END as num FROM face_group g LEFT JOIN ( SELECT group_code, COUNT(*) AS num FROM face_group_detail where status = 0
and create_user_id in
#{item.userId}
GROUP BY group_code ) d ON g.`code` = d.group_code where 1=1
and g.op_user_id=#{opUserId}
and g.name LIKE concat("%",#{name},"%")
and g.op_user_id in
#{item.userId}
order by op_time desc
